import React, { useState, useEffect } from 'react'

const url = 'https://course-api.com/react-tours-project';
const App = () => {
    const [tours, setTours] = useState([]);

    const fetchTours = async () => {
        const response = await fetch(url);
        const tours = response.json();
        setTours(tours);
    }

    useEffect(() => {
        fetchTours();
        console.log(tours);
    }, []);

    return(
        <div>
            {/* {tours.map((tour) => {
                return(
                <p>{tour}</p>
                )
            })} */
            }

        </div>
    )
}

export default App
